function rtnSrnum(n,r){for(var o=0,t=0;t<n.length;t++)o+=Number(n[t]);return sWorsk_idkeychk(Math.floor(Math.random()*o),n,r)}function sWorsk_idkeychk(n,r,o){for(var t=n,a=0;a<r.length;a++)if(null!=o[a]){if(!(r[a]<=t))return a;t-=r[a]}}function shuffle(n){for(var r=n.length,o=r;o>0;o--)n[r-1]=n.splice(Math.floor(Math.random()*o),1)[0];return n}function goSunny(n,r){var o=n+escape(r);window.open(o,"_blank")}var Logpath="http://log.sunnyworks.co.kr/";